import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'locationGroup'
})
export class LocationGroupPipe implements PipeTransform {

    transform(data: any[], filter: string)
    {
      //const groupDataByCity = (data: { locations: { edges: any[]; }; }) => {
      const locations = data.locations.edges.map(edge => edge.node);
      const groupedLocations = locations.reduce((acc, location) => {
        const city = location.address.city;
        if (acc[city]) {
          acc[city].push(location);
        } else {
          acc[city] = [location];
        }
        return acc;
      }, {});
      console.log("groupedLocations",groupedLocations);
      return groupedLocations;

}
